function isFF()
{
    return navigator.userAgent.indexOf("Firefox") > 0 || navigator.userAgent.indexOf("Iceweasel") > 0;
}

function isIE()
{
    return ( navigator.userAgent.indexOf("MSIE") > 0 && navigator.userAgent.indexOf("Opera") < 0 ) || navigator.userAgent.indexOf("Trident/") > 0;
}

