Utils.arrayRemove = function(array, idx)
{
    idx = parseInt(idx);
    if( idx >= 0 )
    {
        if( idx == array.length - 1 )
        {
            delete array[idx];
        }
        for( var i = idx; i < array.length -1; i++ )
        {
            array[i] = array[i+1];
            delete array[i+1];
        }
        array.length--;
    }
}

